import { Add, PlayArrow, ThumbDownOutlined, ThumbUpAltOutlined } from '@material-ui/icons';
import React, { useState } from 'react'
import "./listItem.scss";

export default function ListItem({index}) {
  const [isHovered, setIsHovered] = useState(false);
  const trailer = 
    "https://player.vimeo.com/external/371433846.sd.mp4?s=236da2f3c0fd273d2c6d9a064f3ae35579b2bbdf&profile_id=139&oauth2_token_id=57447761";

  return (
    <div className="listItem"
    style={{left: isHovered && index * 225 -50 + index * 2.5}} 
    onMouseEnter = {() => setIsHovered(true)} 
    onMouseLeave = {() => setIsHovered(false)}
    >
        <img src="https://imgs.search.brave.com/UMBcekC3jYOmPA7KZBPcDWVdZPl1_oPBmMFoGK5NpHQ/rs:fit:1200:1200:1/g:ce/aHR0cHM6Ly9vYnNl/cnZlci5jb20vd3At/Y29udGVudC91cGxv/YWRzL3NpdGVzLzIv/MjAxNS8wMy9ob2Nf/cHMzXzA0MF9oLmpw/Zz9xdWFsaXR5PTgw/JnN0cmlw" 
        alt="" />
        <video src={trailer} autoPlay={true} loop/>
        <div className="itemInfo">
          <div className="icons">
            <PlayArrow />
            <Add />
            <ThumbUpAltOutlined />
            <ThumbDownOutlined />
          </div>

          <div className="itemInfoTop">
            <span>1 hr 14mins</span>
            <span className="limit">+16</span>
            <span>1999</span>
          </div>
          <div className="desc">
            Lorem ipsum dolor sit amet consectetur adipisicing elit. Tenetur debitis provident, repellendus animi natus architecto.
          </div>
          <div className="genre">Action</div>
        </div>
    </div>
  )
}
