import "./app.scss"
import Home from "./pages/home/Home"
import Login from "./pages/login/Login";
import Register from "./pages/register/Register";
import Watch from "./pages/watch/Watch";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  Redirect
}from "react-router-dom";

const App = () => {
  const user = true;
  return(
    <Router>
      <Switch>
      <Route exact path="/">
          {user ? <Home /> : <Redirect to="/register" />}
        </Route>
      <Route path="/register">
      {!user ? <Register /> : <Redirect to="/" />}
        </Route>
      <Route path="/login">
      {!user ? <Login /> : <Redirect to="/register" />}
        </Route>
        { user && (
          <>
          <Route path="/movies">
            <Home type="movies"/>
          </Route>
          <Route path="/series">
            <Home type="series"/>
          </Route>
          <Route path="/watch">
            <Watch/>
          </Route>
        </>
        )
}
      </Switch>
    </Router>
  )
};

export default App;
