import { Add, PlayArrow, ThumbDownOutlined, ThumbUpAltOutlined } from '@material-ui/icons';
import React, { useEffect, useState } from 'react'
import "./listItem.scss";
import axios from 'axios';

export default function ListItem({index, item}) {
  const [isHovered, setIsHovered] = useState(false);

  useEffect(()=>{
    const getMovie = async()=>{
      try{
        const res = await axios.get("/movies/find"+item, {
          headers:{
            token: 
            "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjYzZGFkMGYxYTJjODFhOTRmMTI0ZTk1ZiIsImlzQWRtaW4iOnRydWUsImlhdCI6MTY3NTM3NjMyMiwiZXhwIjoxNjc1ODA4MzIyfQ.ybFRQM3NvnpnoBnMGpOIjh6Pvlb6R0zF8NP1ZAojOGY",
          },
        });
      }catch(err){
        console.log(err)
      }
    }
  }, [])

  return (
    <div className="listItem"
    style={{left: isHovered && index * 225 -50 + index * 2.5}}
    onMouseEnter = {() => setIsHovered(true)} 
    onMouseLeave = {() => setIsHovered(false)}
    >
        <img src={item.img} 
        alt="" />
        {isHovered && (
          <>
          <video src={item.trailer} autoPlay={true} loop/>
          <div className="itemInfo">
            <div className="icons">
              <PlayArrow className="icon"/>
              <Add className="icon"/>
              <ThumbUpAltOutlined className="icon"/>
              <ThumbDownOutlined className="icon"/>
            </div>

            <div className="itemInfoTop">
              <span>{item.duration}</span>
              <span className="limit">+{item.limit}</span>
              <span>{item.year}</span>
            </div>
            <div className="desc">
              {item.desc}
            </div>
            <div className="genre">{item.genre}</div>
          </div></>
        )}
    </div>
  )
}
